  const typingSpeed = 100;    // Velocidad constante de escritura (ms por letra)
  const deletingSpeed = 40;   // Velocidad de borrado (ms gil)
  const pauseTime = 3000;     // Pausa de lectura al completar el texto
  const startDelay = 800;     // Pausa antes de reiniciar el ciclo

  const handleType = useCallback(() => {
    const fullText = phrase;

    // Calculamos el siguiente estado del string
    const nextText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(nextText);

