<script>
document.addEventListener("click", function (e) {
  const el = e.target.closest("a[href^='#']");
  if (!el) return;

  e.preventDefault();
  const target = document.querySelector(el.getAttribute("href"));
  if (target) {
    target.scrollIntoView({ behavior: "smooth" });
  }
});
</script>

